# Example created 08/10/14

# Requires IronRuby 1.1.1 or greater.
# See http://ironruby.net/ for more information.

require 'win32ole'

# Get current path
strPath = File.expand_path(File.dirname(__FILE__)) + &quot;\\&quot;

# Instantiate Object
oTK = WIN32OLE.new(&quot;APToolkit.Object&quot;)

# Create the new PDF file
intOpenOutputFile = oTK.OpenOutputFile(strPath + 'new.pdf')
if intOpenOutputFile != 0
  puts &quot;Error in OpenOutputFile: #{intOpenOutputFile}&quot;
end

# Set whether the fields should be read only in the output PDF
# 0 leave fields as they are, 1 mark all fields as read-only
# Fields set with SetFormFieldData will not be effected
oTK.ReadOnlyOnMerge = 1

# MergeFile is the equivalent of OpenInputFile and CopyForm

# Merge the cover page (0 for all pages)
intMergeFile = oTK.MergeFile(strPath + 'cover.pdf', 0, 0)
if intMergeFile != 1
  puts &quot;Error in MergeFile: #{intMergeFile}&quot;
end

# Merge the second PDF
intMergeFile = oTK.MergeFile(strPath + '5pageLI.pdf', 0, 0)
if intMergeFile != 1
  puts &quot;Error in MergeFile: #{intMergeFile}&quot;
end

# Merge the third PDF
intMergeFile = oTK.MergeFile(strPath + '6pageAA.pdf', 0, 0)
if intMergeFile != 1
  puts &quot;Error in MergeFile: #{intMergeFile}&quot;
end

# Close the new file to complete PDF creation
oTK.CloseOutputFile()

# Release Object
oTK = ''

# Process Complete
puts &quot;Done!&quot;</textarea></form><a href="/Toolkit%20Standard/2011/Merge%20PDF%20files.rb/download" class="tab_button_link"><i class="fa fa-download fa-lg" data-toggle="tooltip" title="Download Example"></i></a><script type="text/javascript">$(".download_example").click(function(){
